import { React, useState } from "react";
import {
  DivFormSecurePlace,
  FormFormSecurePlace,
  TextFieldFormSecurePlace,
  ButtonFormSecurePlace,
} from "./FormSecurePlaceStyles";

export default function FormSecurePlace() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    state: "",
    street: "",
    suburb: "",
    number: "",
  });

  const [formError, setFormError] = useState({
    name: { error: false, message: "" },
    description: { error: false, message: "" },
    state: { error: false, message: "" },
    street: { error: false, message: "" },
    suburb: { error: false, message: "" },
    number: { error: false, message: "" },
  });

  const [loading, setLoading] = useState(false);

  const handleValidation = () => {
    const { name } = form;
    const internalForm = { ...formError };
    let isCorrect = true;

    if (name === "") {
      internalForm.name.error = true;
      internalForm.name.message = "Completa el nombre";
      isCorrect = false;
    } else {
      internalForm.name.error = false;
      internalForm.name.message = "";
    }
    setFormError(internalForm);
    return isCorrect;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    const allFine = handleValidation();
    if (allFine) {
      console.log("Enviando datos");
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    const nameInput = e.target.name;
    const valueInput = e.target.value;

    const internalForm = form;
    internalForm[nameInput] = valueInput;
    setForm(internalForm);
  };

  return (
    <DivFormSecurePlace>
      <FormFormSecurePlace onSubmit={handleSubmit}>
        <h1>Lugares Seguros</h1>
        <TextFieldFormSecurePlace
          error={formError.name.error}
          helperText={formError.name.error&&formError.name.message}
          id="outlined-basic"
          label="Nombre"
          name="name"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Descripcion"
          name="description"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Estado"
          name="state"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Colonia"
          name="suburb"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Calle"
          name="street"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Numero"
          name="number"
          variant="outlined"
          onChange={handleChange}
        />
        <ButtonFormSecurePlace
          type="submit"
          variant="contained"
          disabled={loading}
        >
          {loading ? "Enviando los datos..." : "Enviar datos"}
        </ButtonFormSecurePlace>
      </FormFormSecurePlace>
    </DivFormSecurePlace>
  );
}
