import { React, useState } from "react";
import {
  DivFormSecurePlace,
  FormFormSecurePlace,
  TextFieldFormSecurePlace,
  ButtonFormSecurePlace,
} from "./FormSecurePlaceStyles";

export default function FormSecurePlace() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    state: "",
    street: "",
    suburb: "",
    number: "",
  });

  const [loading, setLoading] = useState(false);

  const handleValidation = ()=>{
    const {name} = form
    let isCorrect = true

    if(name===""){
        console.log("Completa el nombre")
        isCorrect = false
    }

    return isCorrect
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true)
    const allFine = handleValidation()
    if(allFine){
        console.log("Enviando datos");
    }
  };

  const handleChange = (e) => {
    const nameInput = e.target.name;
    const valueInput = e.target.value;

    const internalForm = form;
    internalForm[nameInput] = valueInput;
    setForm(internalForm);
  };

  return (
    <DivFormSecurePlace>
      <FormFormSecurePlace onSubmit={handleSubmit}>
        <h1>Lugares Seguros</h1>
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Nombre"
          name="name"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Descripcion"
          name="description"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Estado"
          name="state"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Colonia"
          name="suburb"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Calle"
          name="street"
          variant="outlined"
          onChange={handleChange}
        />
        <TextFieldFormSecurePlace
          id="outlined-basic"
          label="Numero"
          name="number"
          variant="outlined"
          onChange={handleChange}
        />
        <ButtonFormSecurePlace
          type="submit"
          variant="contained"
          disabled={loading}
        >
          {loading? "Enviando los datos..." : "Enviar datos"}
        </ButtonFormSecurePlace>
      </FormFormSecurePlace>
    </DivFormSecurePlace>
  );
}
